$.fn.magnifierRentgen=function(){return this.each(function(){var i=$(this),n=i.data("image"),a=i.data("image-zoom"),e=i.data("size");i.addClass("magnifierRentgen").resize(function(){i.find(".data-image, magnifier-loupe img").css({width:i.width()})}).append("\r\n			<img class='data-image' src='"+n+"'>\r\n			<div class='magnifier-loupe'>\r\n				<img src='"+a+"'>\r\n			").hover(function(){i.find(".magnifier-loupe").stop().fadeIn()},function(){i.find(".magnifier-loupe").stop().fadeOut()}).find(".data-image").css({width:i.width()}).parent().find(".magnifier-loupe").css({width:e,height:e}).find("img").css({position:"absolute",width:i.width()}),i.mousemove(function(n){var a={},t=i.offset();a={left:n.pageX-t.left-e/2,top:n.pageY-t.top-e/2},i.find(".magnifier-loupe").css({top:a.top,left:a.left}).find("img").css({top:-a.top,left:-a.left,width:i.width()})}),$(window).resize(function(){$(".magnifierRentgen").resize()})})};
